package control;

public class ifelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int ram=20;
    int sham=30;
    
    if(ram>sham)
    {
    	System.out.println("ram greater than sham");
    }
    else
    {
    	System.out.println("sham greater than ram");
    }
	}

}
