package operators;

public class incrdcr {

	public static void main(String[] args) 
	{
		int a = 50;
		int b = 100;
		System.out.println("Print a = "+a);
		System.out.println("Print b = "+b);
		
		System.out.println("--a is "+(--a));
		System.out.println("a-- is "+(a--));
		System.out.println("b++ is "+(b++));
		System.out.println("++b is "+(++b));
		
		
		// TODO Auto-generated method stub

	}

}
